import React, { useState}  from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import NumericInput from 'react-numeric-input';
import Container from 'react-bootstrap/Container';
import Request from './Request'

function Rig() {
	const [nbrRig, setNbrRig] = useState(1);
	const [checkedMan, setCheckedMan] = useState(false);
	const [checkedWoman, setCheckedWoman] = useState(false);
	const api = new Request("http://gopiko.fr/");

	function getRig () {
		api.Call("/rig", "GET", {
			man: checkedMan,
			woman: checkedWoman,
			nbr: nbrRig
		})		
	};

return (
	<div>	
	<div className="d-flex p-2">
		<Form.Check 
			onChange={(e)=>{setCheckedMan(e.target.checked)}}
        		checked={checkedMan}
			type="switch"
        		id="custom-switch"
        		label="man"
     		 />
		<Form.Check 
        		onChange={(e)=>{setCheckedWoman(e.target.checked)}}
        		checked={checkedWoman}
			type="switch"
        		label="woman"
        		id="disabled-custom-switch"
      		/>
		<NumericInput className="form-control" id="ze" min={1} value={nbrRig} onChange={(e)=>{if (e && e >= 1) setNbrRig(e);}} />
		<button onClick={getRig}>Get Rig</button>;
		</div>
		<div></div>
	</div>
	)
}

export default Rig;
