import React, { useState, useEffect}  from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import NumericInput from 'react-numeric-input';
import Container from 'react-bootstrap/Container';
import Request from './Request'
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Card from 'react-bootstrap/Card';

function Rig() {
	let y = 1;
	const [nbrRig, setNbrRig] = useState(1);
	const [checkedMan, setCheckedMan] = useState(false);
	const [checkedWoman, setCheckedWoman] = useState(false);
	const [data, setData] =  useState<any[]>([]);

	const listData =  data.map((n) =>
			<Col>
				<Card style={{ width: '18rem' }}>
					<Card.Body>
						<Card.Header>{n.Name}</Card.Header>
						<Card.Title>{n.Addr}</Card.Title>
						<Card.Text>{n.Postal}</Card.Text>
						<Card.Footer>{n.Tel}</Card.Footer>
					</Card.Body>
				</Card>
			</Col>
  	);	

	const api = new Request("http://gopiko.fr/");	

	useEffect(() => {
		if (y)
  			getRig();
		y = 0;
	}, []);

	function getRig () {
		api.Post("/rig", {
			man: checkedMan,
			woman: checkedWoman,
			nbr: nbrRig.toString()
		}).then((resp: any) => {
			console.log(resp.data)
    			setData(resp.data)
		});		
	};

return (
	<Container fluid>
		<Row>
			<Col>
				<p>Man</p>
				<Form.Check  
					onChange={(e)=>{setCheckedMan(e.target.checked)}}
        				checked={checkedMan}
					type="switch"
        				id="man"
     				 />
				<p>Woman</p>
				<Form.Check 
        				onChange={(e)=>{setCheckedWoman(e.target.checked)}}
        				checked={checkedWoman}
					type="switch"
      				/>
				<p>Number</p>
				<NumericInput className="form-control" id="c-input" min={1} value={nbrRig} onChange={(e)=>{if (e && e >= 1) setNbrRig(e);}} />
				<button onClick={getRig}>Get Rig</button>;
			</Col>
			<Col xs={10}>
				<Row>
				{listData}
				</Row>
			</Col>
		</Row>
	</Container>
	)
}

export default Rig;
